    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long ans = 0;
    while (1) {
        int max_idx = 0;
        for (int i = 1; i < n; i++) {
            if (a[i] > a[max_idx]) {
                max_idx = i;
            }
        }
        if (max_idx == 0) {
            break;
