        long long max_val = a[max_idx];
        long long sum = 0;
        for (int i = 0; i < n; i++) {
            sum += a[i];
        }
        long long diff = max_val - (n - 1);
        long long new_sum = sum - max_val + n;
        long long new_diff = new_sum - (n - 1);
        if (diff == new_diff) {
            break;
        }
        ans++;
        a[max_idx] -= n;
        for (int i = 0; i < n; i++) {
            a[i]++;
        }
    }
    printf("%lld\n", ans);
    return 0;
