from collections import deque

def solve():
    n = int(input())
    p = list(map(int, input().split()))
    p = [x - 1 for x in p]

    in_degree = [0] * n
    for i in range(n):
        in_degree[p[i]] += 1

    queue = deque([i for i in range(n) if in_degree[i] == 0])
    color = [-1] * n
    color[0] = 0

    while queue:
        u = queue.popleft()
        for v in range(n):
            if p[v] == u:
                if color[v] == -1:
                    color[v] = 1 - color[u]
                    queue.append(v)
                elif color[v] == color[u]:
                    print("IMPOSSIBLE")
                    return

    for i in range(n):
        if color[i] == -1:
            color[i] = 0
            for v in range(n):
                if p[v] == i:
                    if color[v] == 0:
                        print("IMPOSSIBLE")
                        return

    print("POSSIBLE")

if __name__ == "__main__":
