import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] p;
    static int[] in;
    static int[] out;
    static int[] color;
    static int[] depth;
    static int[] parent;
    static int[] size;
    static int[] maxDepth;
    static int[] maxDepthParent;
    static int[] maxDepthSize;
    static int[] maxDepthColor;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        p = new int[N + 1];
        in = new int[N + 1];
        out = new int[N + 1];
        color = new int[N + 1];
        depth = new int[N + 1];
        parent = new int[N + 1];
        size = new int[N + 1];
        maxDepth = new int[N + 1];
        maxDepthParent = new int[N + 1];
        maxDepthSize = new int[N + 1];
        maxDepthColor = new int[N + 1];

        for (int i = 1; i <= N; i++) {
            p[i] = Integer.parseInt(st.nextToken());
            in[p[i]]++;
            out[i]++;
        }

        for (int i = 1; i <= N; i++) {
            if (in[i] == 0) {
                dfs(i, 0);
            }
        }

        for (int i = 1; i <= N; i++) {
            out[i]--;
        }

        for (int i = 1; i <= N; i++) {
            if (out[i] == 0) {
                dfs(i, 0);
            }
        }

        int max = 0;
        for (int i = 1; i <= N; i++) {
            max = Math.max(max, maxDepth[i]);
        }

        if (max == 1) {
            pw.println("POSSIBLE");
        } else {
            boolean ok = true;
            for (int i = 1; i <= N; i++) {
                if (maxDepth[i] == max) {
                    if (maxDepthColor[i] == maxDepthColor[maxDepthParent[i]]) {
                        ok = false;
                        break;
                    }
                }
            }

            if (ok) {
                pw.println("POSSIBLE");
            } else {
                pw.println("IMPOSSIBLE");
            }
        }

        pw.close();
    }

    static void dfs(int u, int d) {
        depth[u] = d;
        parent[u] = 0;
        size[u] = 1;
        maxDepth[u] = d;
        maxDepthParent[u] = 0;
        maxDepthSize[u] = 1;
        maxDepthColor[u] = color[u];

        for (int v = p[u]; v != 0; v = p[u]) {
            u = v;
            dfs(u, d + 1);
            size[u] = size[parent[u]] + size[u];
            if (size[u] > size[maxDepthParent[d]]) {
                maxDepthParent[d] = u;
                maxDepthSize[d] = size[u];
                maxDepthColor[d] = color[u];
            }
            if (maxDepth[u] > maxDepth[d]) {
                maxDepth[d] = maxDepth[u];
            }
            parent[u] = d;
        }
    }
}
