            cout << "POSSIBLE" << endl;
            return 0;
        }
    }

    if (a[0] == k || a[n - 1] == k) {
        cout << "POSSIBLE" << endl;
        return 0;
    }

    bool found = false;
    for (int i = 0; i < n - 1; i++) {
        if ((k - abs(a[i] - a[i + 1])) % 2 == 0) {
            found = true;
            break;
        }
    }

    if (found) {
        cout << "POSSIBLE" << endl;
    } else {
        cout << "IMPOSSIBLE" << endl;
    }

    return 0;
