const int N = 1e5 + 5;

int n;
vector<pair<int, int>> adj[N];
int dist[N];

void dfs(int u, int p, int& maxDist, int& maxNode) {
    maxDist = max(maxDist, dist[u]);
    maxNode = max(maxNode, u);
    for (auto& [v, w] : adj[u]) {
        if (v != p) {
            dist[v] = dist[u] + w;
            dfs(v, u, maxDist, maxNode);
        }
    }
}

