    dfs(1, 0, maxDist, maxNode);

    int maxDist2 = 0;
    int maxNode2 = 0;
    dfs(maxNode, 0, maxDist2, maxNode2);

    cout << max(maxDist + maxDist2, max(maxDist, maxDist2) + n - 1) << '\n';

    return 0;
}
