#include <math.h>

#define MOD 1000000007
#define N 1000000

typedef long long ll;

ll nCr(ll n, ll r) {
    ll res = 1;
    for (ll i = 1; i <= r; i++) {
        res = (res * (n - i + 1)) % MOD;
        res = (res * pow(i, MOD - 2)) % MOD;
    }
    return res;
}

int main() {
    ll x1, x2, x3, x4, x5, x6, y1, y2, y3, y4, y5, y6;
    scanf("%lld %lld %lld %lld %lld %lld %lld %lld %lld %lld %lld %lld", &x1, &x2, &x3, &x4, &x5, &x6, &y1, &y2, &y3, &y4, &y5, &y6);

    ll ans = 0;
    for (ll i = x3 - 1; i <= x4 - 1; i++) {
        for (ll j = y3 - 1; j <= y4 - 1; j++) {
            ll dx = x6 - x5;
            ll dy = y6 - y5;
            ll x = x5 + dx;
            ll y = y5 + dy;
            ll res = 1;
            for (ll k = 0; k <= dx; k++) {
                res = (res * (x - x1 + 1)) % MOD;
                res = (res * (x - x2 + 1)) % MOD;
                x--;
            }
            for (ll k = 0; k <= dy; k++) {
                res = (res * (y - y1 + 1)) % MOD;
                res = (res * (y - y2 + 1)) % MOD;
                y--;
            }
            res = (res * nCr(x2 - x1 + y2 - y1, x2 - x1)) % MOD;
            res = (res * nCr(x4 - x3 + y4 - y3, x4 - x3)) % MOD;
            ans = (ans + res) % MOD;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
