long long binomial_coefficient(int n, int k) {
    vector<long long> fact(n + 1);
    fact[0] = 1;
    for (int i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % MOD;
    }
    return (fact[n] * pow(fact[k], MOD - 2) % MOD * pow(fact[n - k], MOD - 2) % MOD) % MOD;
}

int main() {
    int x1, x2, x3, x4, x5, x6, y1, y2, y3, y4, y5, y6;
    cin >> x1 >> x2 >> x3 >> x4 >> x5 >> x6 >> y1 >> y2 >> y3 >> y4 >> y5 >> y6;

    long long ans = 0;
    for (int i = x3 - x1 + 1; i <= x4 - x2 + 1; i++) {
        for (int j = y3 - y1 + 1; j <= y4 - y2 + 1; j++) {
            ans += binomial_coefficient(x4 - x3, i) * binomial_coefficient(x5 - x3 - 1, i - 1) % MOD
                   * binomial_coefficient(y4 - y3, j) * binomial_coefficient(y5 - y3 - 1, j - 1) % MOD
                   * (x6 - x5 + 1) * (y6 - y5 + 1);
            ans %= MOD;
        }
    }
    cout << ans << endl;
    return 0;
}
