import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 1000000;

    static long[] fact = new long[MAXN + 1];
    static long[] inv = new long[MAXN + 1];

    static long binom(int n, int k) {
        return (fact[n] * inv[k] % MOD) * inv[n - k] % MOD;
    }

    static long C(int n, int k) {
        return binom(n + k - 1, k);
    }

    static long solve(int n, int m, int x1, int x2, int x3, int x4, int x5, int x6, int y1, int y2, int y3, int y4, int y5, int y6) {
        long ans = 0;
        for (int i = x1; i <= x2; i++) {
            for (int j = y1; j <= y2; j++) {
                long ways = C(x2 - i + y2 - j, x2 - i) * C(y6 - y5 + y2 - j, y6 - y5) % MOD;
                for (int k = x3; k <= x4; k++) {
                    for (int l = y3; l <= y4; l++) {
                        long ways2 = C(x4 - k + y4 - l, x4 - k) * C(y6 - y5 + y4 - l, y6 - y5) % MOD;
                        for (int p = x5; p <= x6; p++) {
                            for (int q = y5; q <= y6; q++) {
                                long ways3 = C(x6 - p + y6 - q, x6 - p) * C(y6 - y5 + y6 - q, y6 - y5) % MOD;
                                ans = (ans + ways * ways2 * ways3) % MOD;
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int x1 = Integer.parseInt(st.nextToken());
        int x2 = Integer.parseInt(st.nextToken());
        int x3 = Integer.parseInt(st.nextToken());
        int x4 = Integer.parseInt(st.nextToken());
        int x5 = Integer.parseInt(st.nextToken());
        int x6 = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        int y1 = Integer.parseInt(st.nextToken());
        int y2 = Integer.parseInt(st.nextToken());
        int y3 = Integer.parseInt(st.nextToken());
        int y4 = Integer.parseInt(st.nextToken());
        int y5 = Integer.parseInt(st.nextToken());
        int y6 = Integer.parseInt(st.nextToken());
        fact[0] = 1;
        for (int i = 1; i <= MAXN; i++) {
            fact[i] = (fact[i - 1] * i) % MOD;
        }
        inv[MAXN] = pow(fact[MAXN], MOD - 2);
        for (int i = MAXN - 1; i >= 0; i--) {
            inv[i] = (inv[i + 1] * (i + 1)) % MOD;
        }
        System.out.println(solve(x1, y1, x2, x2, x3, x4, x5, x6, y2, y2, y3, y4, y5, y6));
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % MOD;
            }
            a = (a * a) % MOD;
            b >>= 1;
        }
        return res;
    }
