from collections import deque

def solve():
    n = int(input())
    a = list(map(lambda x: int(x) - 1, input().split()))
    b = list(map(lambda x: int(x) - 1, input().split()))

    a_root = -1
    for i in range(n):
        if a[i] == -1:
            a_root = i
            break

    b_root = -1
    for i in range(n):
        if b[i] == -1:
            b_root = i
            break

    a_deg = [0] * n
    b_deg = [0] * n
    for i in range(n):
        a_deg[a[i]] += 1
        b_deg[b[i]] += 1

    a_q = deque()
    b_q = deque()
    for i in range(n):
        if a_deg[i] == 0:
            a_q.append(i)
        if b_deg[i] == 0:
            b_q.append(i)

    a_color = [-1] * n
    b_color = [-1] * n
    a_color[a_root] = 0
    b_color[b_root] = 0

    while a_q:
        u = a_q.popleft()
        for v in range(n):
            if a[v] == u:
                a_deg[v] -= 1
                if a_color[v] == -1:
                    a_color[v] = 1 - a_color[u]
                    a_q.append(v)
                elif a_color[v] != 1 - a_color[u]:
                    print("IMPOSSIBLE")
                    return

    while b_q:
        u = b_q.popleft()
        for v in range(n):
            if b[v] == u:
                b_deg[v] -= 1
                if b_color[v] == -1:
                    b_color[v] = 1 - b_color[u]
                    b_q.append(v)
                elif b_color[v] != 1 - b_color[u]:
                    print("IMPOSSIBLE")
                    return

    for i in range(n):
        if a_color[i] == -1:
            a_color[i] = 0
        if b_color[i] == -1:
            b_color[i] = 0

    print("POSSIBLE")
    print(*[1 if a_color[i] else -1 for i in range(n)])
