        for (int i = 1; i <= N; i++) {
            B[i] = Integer.parseInt(st.nextToken());
            adj[B[i]].add(i);
        }

        dfs(1, 0);
        dfs2(1, 0);

        if (color[1] == 0) {
            pw.println("POSSIBLE");
            for (int i = 1; i <= N; i++) {
                pw.print(ans[i] + " ");
            }
            pw.println();
