        if (color == 1) {
            graph[node].stream().filter(p -> p.first != parent).forEach(p -> {
                if (color[p.second] == 0) {
                    color[p.second] = 2;
                }
            });
        } else {
            graph[node].stream().filter(p -> p.first != parent).forEach(p -> {
                if (color[p.second] == 0) {
                    color[p.second] = 1;
                }
            });
        }
        for (Pair p : graph[node]) {
            if (p.first != parent) {
                dfs(p.second, node, color[p.second]);
            }
        }
    }

    public static void main(String[] args) {
        FastReader in = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        n = in.nextInt();
        graph = new ArrayList[n];
        color = new int[n];
        for (int i = 0; i < n; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int i = 0; i < n - 1; i++) {
            int u = in.nextInt() - 1;
            int v = in.nextInt() - 1;
            graph[u].add(new Pair(v, 1));
            graph[v].add(new Pair(u, 1));
        }
        color[0] = 1;
        color[n - 1] = 2;
        dfs(0, -1, color[0]);
        if (color[n - 1] == 1) {
            out.println("Fennec");
        } else {
            out.println("Snuke");
        }
        out.close();
    }
}
