from collections import deque

def solve():
    n = int(input())
    graph = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        graph[u].append(v)
        graph[v].append(u)

    color = [0] * (n + 1)
    color[1] = 1
    color[n] = 2

    queue = deque([(1, 1), (n, 2)])
    while queue:
        u, c = queue.popleft()
        for v in graph[u]:
            if color[v] == 0:
                if c == 1:
                    color[v] = 2
                    queue.append((v, 2))
                else:
                    color[v] = 1
                    queue.append((v, 1))

    if color[n] == 1:
