import sys

def ask(n):
    print(f'? {n}', flush=True)
    return input().strip()

def answer(n):
    print(f'! {n}', flush=True)
    sys.exit(0)

def check(n):
    ans = ask(n)
    if ans == 'Y':
        return True
    else:
        return False

def binary_search(low, high):
    while low <= high:
        mid = (low + high) // 2
        if check(mid):
            high = mid - 1
        else:
            low = mid + 1
    return low

