#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAX_N = 16;
const int INF = numeric_limits<int>::max();

int n, m;
vector<vector<pair<int, int>>> adj(MAX_N);
int dist[MAX_N][MAX_N];

void dijkstra(int s, int t) {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dist[i][j] = INF;
        }
    }
    dist[s][s] = 0;
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.push({0, s});
    while (!pq.empty()) {
        int d = pq.top().first;
        int u = pq.top().second;
        pq.pop();
        if (d > dist[s][u]) continue;
        for (auto& [v, w] : adj[u]) {
            if (dist[s][v] > dist[s][u] + w) {
                dist[s][v] = dist[s][u] + w;
                pq.push({dist[s][v], v});
            }
        }
    }
}

int main() {
    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        u--; v--;
        adj[u].push_back({v, w});
        adj[v].push_back({u, w});
    }
    dijkstra(0, n - 1);
    int ans = INF;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) continue;
            ans = min(ans, dist[i][j] + dist[j][n - 1] - dist[i][n - 1]);
        }
    }
    cout << ans << endl;
    return 0;
}
