#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n, m;
int a[MAXN];
int x[MAXN], y[MAXN];
int cnt[MAXN];
int ans;

void update(int pos, int val) {
    while (pos <= n) {
        cnt[pos] = val;
        pos += pos & -pos;
    }
}

int query(int pos) {
    int res = 0;
    while (pos) {
        res += cnt[pos];
        pos -= pos & -pos;
    }
    return res;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
        update(a[i], 1);
    }
    for (int i = 1; i <= m; i++) {
        scanf("%d %d", &x[i], &y[i]);
        ans = query(n);
        ans -= query(x[i]) - query(x[i] - 1);
        if (x[i] != y[i]) {
            update(x[i], -1);
            update(y[i], 1);
        }
        printf("%d\n", ans);
    }
    return 0;
}
