            }
        }

        int res = 0;
        for (int j = 1; j <= n; j++) {
            if (s.find(j) != s.end()) {
                res += cnt[j] - mx;
            }
        }

        ans[i] = res;
    }

    for (int i = 0; i < m; i++) {
        cout << ans[i] << endl;
    }

    return 0;
}
