import java.io.*;
import java.util.*;

public class Main {
    static class Reader {
        final private int BUFFER_SIZE = 1 << 16;
        private DataInputStream din;
        private byte[] buffer;
        private int bufferPointer, bytesRead;

        public Reader() {
            din = new DataInputStream(System.in);
            buffer = new byte[BUFFER_SIZE];
            bufferPointer = bytesRead = 0;
        }

        public Reader(String file_name) throws IOException {
            din = new DataInputStream(new FileInputStream(file_name));
            buffer = new byte[BUFFER_SIZE];
            bufferPointer = bytesRead = 0;
        }

        public String readLine() throws IOException {
            byte[] buf = new byte[64];
            int cnt = 0, c;
            while ((c = read()) != -1) {
                if (c == '\n') {
                    if (cnt != 0) {
                        break;
                    } else {
                        continue;
                    }
                }
                buf[cnt++] = (byte) c;
            }
            return new String(buf, 0, cnt);
        }

        public int nextInt() throws IOException {
            int ret = 0;
            byte c = read();
            while (c <= ' ') {
                c = read();
            }
            boolean neg = (c == '-');
            if (neg)
                c = read();
            do {
                ret = ret * 10 + c - '0';
            } while ((c = read()) >= '0' && c <= '9');

            if (neg)
                return -ret;
            return ret;
        }

        public long nextLong() throws IOException {
            long ret = 0;
            byte c = read();
            while (c <= ' ')
                c = read();
            boolean neg = (c == '-');
            if (neg)
                c = read();
            do {
                ret = ret * 10 + c - '0';
            } while ((c = read()) >= '0' && c <= '9');
            if (neg)
                return -ret;
            return ret;
        }

        public double nextDouble() throws IOException {
            double ret = 0, div = 1;
            byte c = read();
            while (c <= ' ')
                c = read();
            boolean neg = (c == '-');
            if (neg)
                c = read();

            do {
                ret = ret * 10 + c - '0';
            } while ((c = read()) >= '0' && c <= '9');

            if (c == '.') {
                while ((c = read()) >= '0' && c <= '9') {
                    ret += (c - '0') / (div *= 10);
                }
            }

            if (neg)
                return -ret;
            return ret;
        }

        private void fillBuffer() throws IOException {
            bytesRead = din.read(buffer, bufferPointer = 0, BUFFER_SIZE);
            if (bytesRead == -1)
                buffer[0] = -1;
        }

        private byte read() throws IOException {
            if (bufferPointer == bytesRead)
                fillBuffer();
            return buffer[bufferPointer++];
        }

        public void close() throws IOException {
            if (din == null)
                return;
            din.close();
        }
    }

    static class SegTree {
        int n;
        int[] tree;
        int[] lazy;

        public SegTree(int[] a) {
            n = a.length;
            tree = new int[4 * n];
            lazy = new int[4 * n];
            build(1, 0, n - 1, a);
        }

        private void build(int v, int tl, int tr, int[] a) {
            if (tl == tr) {
                tree[v] = a[tl];
            } else {
                int tm = (tl + tr) / 2;
                build(2 * v, tl, tm, a);
                build(2 * v + 1, tm + 1, tr, a);
                tree[v] = Math.max(tree[2 * v], tree[2 * v + 1]);
            }
        }

        public void update(int v, int tl, int tr, int l, int r, int val) {
            if (l > r)
                return;
            if (l == tl && r == tr) {
                lazy[v] = Math.max(lazy[v], val);
            } else {
                int tm = (tl + tr) / 2;
                propagate(v, tl, tr);
                update(2 * v, tl, tm, l, Math.min(r, tm), val);
                update(2 * v + 1, tm + 1, tr, Math.max(l, tm + 1), r, val);
                tree[v] = Math.max(tree[2 * v], tree[2 * v + 1]);
            }
        }

        private void propagate(int v, int tl, int tr) {
            if (lazy[v] != 0) {
                tree[v] = lazy[v];
                if (tl != tr) {
                    lazy[2 * v] = lazy[v];
                    lazy[2 * v + 1] = lazy[v];
                }
                lazy[v] = 0;
            }
        }

        public int query(int v, int tl, int tr, int l, int r) {
            if (l > r)
                return 0;
            if (l == tl && r == tr)
                return tree[v];
            int tm = (tl + tr) / 2;
            propagate(v, tl, tr);
            return Math.max(query(2 * v, tl, tm, l, Math.min(r, tm)),
                    query(2 * v + 1, tm + 1, tr, Math.max(l, tm + 1), r));
        }
    }

    public static void main(String[] args) throws IOException {
        Reader sc = new Reader();
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int[] x = new int[m];
        int[] y = new int[m];
        for (int i = 0; i < m; i++) {
            x[i] = sc.nextInt() - 1;
            y[i] = sc.nextInt() - 1;
        }
        SegTree seg = new SegTree(a);
        for (int i = 0; i < m; i++) {
            seg.update(1, 0, n - 1, x[i], x[i], y[i]);
            System.out.println(n - seg.query(1, 0, n - 1, 0, n - 1));
        }
    }
}
