using namespace std;

const int MAXN = 1e5 + 5;
const int MAXH = 200;

struct Piece {
    int A, B, C, D;
};

bool check(vector<Piece>& pieces, int H, int n) {
    vector<int> dp(H + 1, 0);
    dp[0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = H; j >= 0; j--) {
            if (dp[j] == 1) {
                int k = min(j + pieces[i].A, H);
                if (pieces[i].C == 0) {
                    dp[k] = 1;
                } else {
                    int l = min(k + pieces[i].D, H);
                    dp[l] = 1;
                }
            }
        }
    }
    return dp[H] == 1;
}

