    }
    sort(pieces.begin(), pieces.end(), [](const Piece& a, const Piece& b) {
        return a.A + a.C < b.A + b.C;
    });
    if (check(pieces, H, n)) {
        cout << "YES" << endl;
    } else {
        cout << "NO" << endl;
    }
