import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int[][] dp;
    static int[][][] memo;
    static int[][] A, B, C;
    static int N, M, K;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        A = new int[K][3];
        B = new int[K][3];
        C = new int[K][3];
        for (int i = 0; i < K; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 0; j < 3; j++) {
                A[i][j] = Integer.parseInt(st.nextToken());
            }
        }
        dp = new int[M + 1][N + 1];
        memo = new int[M + 1][N + 1][1 << (N + 1)];
        for (int i = 0; i <= M; i++) {
            for (int j = 0; j <= N; j++) {
                Arrays.fill(memo[i][j], -1);
            }
        }
        System.out.println(solve(1, 1));
    }

    static int solve(int m, int n) {
        if (n == N + 1) return 1;
        if (m > M) return 0;
        if (dp[m][n] != 0) return dp[m][n];
        int mask = (1 << (N + 1)) - 1;
        int res = 0;
        for (int i = 0; i <= mask; i++) {
            if (check(m, n, i)) {
                res = (res + solve(m + 1, n + 1)) % MOD;
            }
        }
        dp[m][n] = res;
        return res;
    }

    static boolean check(int m, int n, int mask) {
        if (memo[m][n][mask] != -1) return memo[m][n][mask] == 1;
        boolean res = true;
        for (int i = 0; i < K; i++) {
            int a = A[i][0] - 1, b = B[i][0] - 1, c = C[i][0];
            if ((mask & (1 << b)) == 0) {
                if (c == 0 && (mask & (1 << (b + 1))) == 0) {
                    res = false;
                    break;
                }
                if (c == 1 && (mask & (1 << (b + 1))) == 0) {
                    res = false;
                    break;
                }
            }
        }
        memo[m][n][mask] = res ? 1 : 0;
        return res;
    }
}
