            for (int j = 1; j <= cnt[i]; j++) {
                dp[i] = (int) ((dp[i] + (long) dp[i - j] * fac(j - 1) % MOD * fac(cnt[i] - j + 1) % MOD) % MOD);
            }
            pw.println(dp[i]);
        }
        pw.close();
    }

    static int fac(int x) {
        int res = 1;
        for (int i = 1; i <= x; i++) {
            res = (int) ((res * (long) i) % MOD);
        }
        return res;
    }
