from collections import defaultdict, deque

def solve():
    n = int(input())
    graph = defaultdict(list)
    in_degree = [0] * (n + 1)
    for i in range(2, n + 1):
        a = int(input())
        graph[a].append(i)
        in_degree[i] += 1
    queue = deque()
    for i in range(1, n + 1):
        if in_degree[i] == 0:
            queue.append(i)
    count = 0
    while queue:
        node = queue.popleft()
        count += 1
        for neighbor in graph[node]:
            in_degree[neighbor] -= 1
            if in_degree[neighbor] == 0:
                queue.append(neighbor)
    if in_degree[2] == 0:
        print(count - 1)
    else:
        print(-1)

t = 1
for _ in range(t):
    solve()
