        PrintWriter out = new PrintWriter(System.out);

        int n = fs.nextInt();
        int[] a = fs.readArray(n);
        int[] count = new int[n + 1];
        int[] depth = new int[n + 1];
        Arrays.fill(depth, -1);
        depth[1] = 0;
        for (int i = 0; i < n; i++) {
            count[a[i]]++;
            if (depth[a[i]] == -1) depth[a[i]] = depth[a[i - 1]] + 1;
        }
        int ans = 0;
        if (depth[2] == -1) {
            int[] stack = new int[n + 1];
            int top = 0;
            stack[top++] = 1;
            while (top > 0) {
                int u = stack[--top];
                for (int v = 1; v <= n; v++) {
                    if (count[v] > 0 && depth[v] == -1 && v != u) {
                        depth[v] = depth[u] + 1;
                        stack[top++] = v;
                    }
                }
            }
            if (depth[2] == -1) out.println(-1);
            else {
                int u = 2;
                while (u != 1) {
                    ans += depth[u] - depth[a[u - 1]];
                    u = a[u - 1];
                }
                out.println(ans);
            }
        } else {
            int u = 2;
            while (u != 1) {
                ans += depth[u] - depth[a[u - 1]];
                u = a[u - 1];
            }
            out.println(ans);
        }
        out.close();
    }
}
