#define MOD 1000000007

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    long long fac[n + 1], inv[n + 1];
    fac[0] = 1;
    for (int i = 1; i <= n; i++) fac[i] = (fac[i - 1] * i) % MOD;
    inv[n] = power(fac[n], MOD - 2);
    for (int i = n - 1; i >= 0; i--) inv[i] = (inv[i + 1] * (i + 1)) % MOD;
    long long ans = 1;
    for (int i = 0; i < n / 2; i++) {
        ans = (ans * fac[n - 2 * i - 1]) % MOD;
        ans = (ans * fac[m - 1]) % MOD;
        ans = (ans * fac[m - 1]) % MOD;
        ans = (ans * inv[n - 2 * i - 1]) % MOD;
        ans = (ans * inv[m - 1]) % MOD;
        ans = (ans * inv[m - 1]) % MOD;
    }
    for (int i = 0; i < m / 2; i++) {
        ans = (ans * fac[n - 1]) % MOD;
        ans = (ans * fac[m - 2 * i - 1]) % MOD;
        ans = (ans * inv[n - 1]) % MOD;
        ans = (ans * inv[m - 2 * i - 1]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
