    for (int i = 0; i < n; i++) {
        int x = p[i].first;
        int y = p[i].second;
        for (int j = i + 1; j < n; j++) {
            int dx = abs(x - p[j].first);
            int dy = abs(y - p[j].second);
            int d = min(dx, dy);
            if (find(i) != find(j)) {
                unionSet(i, j);
                ans += d;
            }
        }
    }
