def factorial(n):
    f = [1] * (n + 1)
    for i in range(1, n + 1):
        f[i] = f[i - 1] * i % MOD
    return f

def ncr(n, r, f):
    return f[n] * pow(f[n - r], MOD - 2, MOD) % MOD * pow(f[r], MOD - 2, MOD) % MOD

def solve():
