    f = factorial(n + m)
    ans = 0
    for i in range(n + 1):
        ans += ncr(n, i, f) * ncr(m, i, f) * ncr(n + m - 2 * i, m - i, f)
        ans %= MOD
    print(ans)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
