import sys
import bisect

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, start, end, index, value):
        if start == end:
            self.tree[node] = value
            return
        mid = (start + end) // 2
        if index <= mid:
            self.update(2 * node, start, mid, index, value)
        else:
            self.update(2 * node + 1, mid + 1, end, index, value)
        self.tree[node] = max(self.tree[2 * node], self.tree[2 * node + 1])

    def query(self, node, start, end, left, right):
        if start > right or end < left:
            return 0
        if start >= left and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return max(self.query(2 * node, start, mid, left, right),
                   self.query(2 * node + 1, mid + 1, end, left, right))

def solve():
    n, m = map(int, sys.stdin.readline().split())
    intervals = []
    for _ in range(n):
        l, r = map(int, sys.stdin.readline().split())
        intervals.append((l, r))
    intervals.sort(key=lambda x: x[1])
    st = SegmentTree(m + 1)
    ans = 0
    for l, r in intervals:
        idx = bisect.bisect_left([i[1] for i in intervals], r)
        if idx < n:
            idx = bisect.bisect_left([i[1] for i in intervals[idx:]], r)
            idx += idx
        else:
            idx = m + 1
        ans += max(0, idx - st.query(1, 0, m, l, idx - 1))
        st.update(1, 0, m, idx, idx)
    print(ans)

if __name__ == "__main__":
    solve()
