int n, m;
int dp[1 << 15][15];
int mask[15];
int x[15], y[15];

int dfs(int mask, int pos) {
    if (pos == n) return !mask;
    if (dp[mask][pos] != -1) return dp[mask][pos];
    int res = 0;
    for (int i = 0; i < n; i++) {
        if ((mask >> i) & 1) continue;
        if (mask & (1 << pos)) continue;
        if (pos == 0 || (mask & (1 << (pos - 1)))) continue;
        if (x[pos] == i + 1) {
            res = (res + dfs(mask | (1 << i), pos + 1)) % MOD;
        }
    }
    return dp[mask][pos] = res;
}

