public class Main {
    static final int N = 15;
    static final int M = N * (N - 1) / 2 + 1;
    static final int MD = 1000000007;

    static int[][] dp = new int[M][1 << N];
    static int[][] dp2 = new int[M][1 << N];
    static int[][] to = new int[M][N];
    static int[] deg = new int[N];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);

        int n = sc.nextInt();
        int m = sc.nextInt();
        for (int i = 0; i < m; i++) {
            int x = sc.nextInt() - 1;
            int y = sc.nextInt() - 1;
            to[i][x] = y;
            deg[y]++;
        }

        for (int i = 0; i < n; i++) {
            dp[0][1 << i] = 1;
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < (1 << n); j++) {
                if (dp[i][j] == 0) continue;
                for (int k = 0; k < n; k++) {
                    if ((j & (1 << k)) != 0) continue;
                    if (deg[k] == 0) continue;
                    int u = to[i][k];
                    int v = j | (1 << u);
                    dp2[i + 1][v] = (dp2[i + 1][v] + dp[i][j]) % MD;
                }
            }
            for (int j = 0; j < (1 << n); j++) {
                dp[i + 1][j] = dp2[i + 1][j];
                dp2[i + 1][j] = 0;
            }
        }

        int ans = 0;
        for (int i = 0; i < (1 << n); i++) {
            ans = (ans + dp[m][i]) % MD;
        }

        pw.println(ans);
        pw.close();
    }

    static class Scanner {
        BufferedReader br;
        StringTokenizer st;

        Scanner(InputStream in) {
            br = new BufferedReader(new InputStreamReader(in));
        }

        String next() throws IOException {
            while (st == null || !st.hasMoreTokens()) {
                st = new StringTokenizer(br.readLine());
            }
            return st.nextToken();
        }

        int nextInt() throws IOException {
            return Integer.parseInt(next());
        }
    }
}
