#define MAX_RATING 4800
#define MAX_COLORS 8

int rating_to_color(int rating) {
    if (rating <= 399) return 0;
    if (rating <= 799) return 1;
    if (rating <= 1199) return 2;
    if (rating <= 1599) return 3;
    if (rating <= 1999) return 4;
    if (rating <= 2399) return 5;
    if (rating <= 2799) return 6;
    if (rating <= 3199) return 7;
    return 7; // for ratings 3200 and above
}

int main() {
    int n;
    scanf("%d", &n);

    int colors[MAX_RATING];
    for (int i = 0; i < MAX_RATING; i++) {
        colors[i] = -1;
    }

    int max_colors = 0;
    int min_colors = 0;

    for (int i = 0; i < n; i++) {
        int rating;
        scanf("%d", &rating);

        int color = rating_to_color(rating);
        if (colors[rating] == -1) {
            colors[rating] = color;
            max_colors++;
        } else {
            if (color != colors[rating]) {
                min_colors++;
            }
        }
    }

    printf("%d %d\n", min_colors, max_colors);

    return 0;
}
