#include <stdio.h>
#include <string.h>

int main() {
    int n;
    char s[105];
    scanf("%d %s", &n, s);
    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == '(') cnt++;
        else cnt--;
        if (cnt < 0) {
            cnt = 0;
            int j = i;
            while (j < n && s[j] == ')') j++;
            for (int k = i; k <= j; k++) {
                if (s[k] == '(') s[k] = ')';
                else s[k] = '(';
            }
            i = j;
        }
    }
    int len = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == '(') len++;
        else len--;
    }
    if (len > 0) {
        int j = 0;
        while (j < n && s[j] == ')') j++;
        for (int k = 0; k < len; k++) {
            s[j++] = '(';
        }
        for (int k = j; k < n; k++) {
            s[k] = ')';
        }
    }
    printf("%s\n", s);
    return 0;
}

