std::string solve(int n, std::string s) {
    int cnt = 0;
    for (char c : s) {
        if (c == '(') cnt++;
        else cnt--;
        if (cnt < 0) return "()";
    }
    if (cnt == 0) return s;
    std::string res = "";
    for (int i = 0; i < cnt; i++) res += "(";
    for (int i = 0; i < cnt; i++) res += ")";
    for (char c : s) {
        if (c == '(') res += "(";
        else res += ")";
    }
    return res;
}

int main() {
    int n;
    std::string s;
    std::cin >> n >> s;
    std::cout << solve(n, s) << std::endl;
    return 0;
}
