int main() {
    int n, a, b;
    scanf("%d %d %d", &n, &a, &b);

    int h[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &h[i]);
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        int min = h[i];
        for (int j = 0; j < n; j++) {
            if (i != j && h[j] < min) {
                min = h[j];
            }
        }
        int diff = h[i] - min;
        if (diff % (a - b) == 0) {
            ans += diff / (a - b);
        } else {
            ans += diff / (a - b) + 1;
        }
    }

    printf("%d\n", ans);
    return 0;
}
