#include <stdio.h>
#include <stdlib.h>

#define MAXN 2005
#define MAXM 2005
#define MAXQ 200005

int n, m, q;
int s[MAXN][MAXM];
int vis[MAXN][MAXM];
int cnt[MAXN][MAXM];
int ans[MAXQ];

void dfs(int x, int y) {
    if (x < 1 || x > n || y < 1 || y > m || vis[x][y] || !s[x][y]) return;
    vis[x][y] = 1;
    cnt[x][y] = 1;
    dfs(x + 1, y);
    dfs(x - 1, y);
    dfs(x, y + 1);
    dfs(x, y - 1);
}

void solve(int x1, int y1, int x2, int y2) {
    int i, j;
    for (i = x1; i <= x2; i++) {
        for (j = y1; j <= y2; j++) {
            vis[i][j] = 0;
            cnt[i][j] = 0;
        }
    }
    for (i = x1; i <= x2; i++) {
        for (j = y1; j <= y2; j++) {
            if (s[i][j] && !vis[i][j]) {
                dfs(i, j);
                cnt[i][j] = 1;
            }
        }
    }
    int res = 0;
    for (i = x1; i <= x2; i++) {
        for (j = y1; j <= y2; j++) {
            res += cnt[i][j];
        }
    }
    ans[q - 1] = res;
}

int main() {
    int i, j;
    scanf("%d%d%d", &n, &m, &q);
    for (i = 1; i <= n; i++) {
        for (j = 1; j <= m; j++) {
            scanf("%d", &s[i][j]);
        }
    }
    for (i = 1; i <= q; i++) {
        int x1, y1, x2, y2;
        scanf("%d%d%d%d", &x1, &y1, &x2, &y2);
        solve(x1, y1, x2, y2);
    }
    for (i = 1; i <= q; i++) {
        printf("%d\n", ans[i - 1]);
    }
    return 0;
}
