#include <iostream>
#include <vector>
#include <algorithm>
#include <cstdio>

using namespace std;

const int N = 2005;

int n, m, q;
int s[N][N];
int f[N][N];
int dfn[N * N];
int low[N * N];
int cnt;
int st[N * N];
int top;
int id[N * N];
int ans[N * N];
vector<int> g[N * N];

void tarjan(int u) {
    dfn[u] = low[u] = ++cnt;
    st[++top] = u;
    id[u] = top;
    for (int v : g[u]) {
        if (!dfn[v]) {
            tarjan(v);
            low[u] = min(low[u], low[v]);
        } else if (id[v]) {
            low[u] = min(low[u], dfn[v]);
        }
    }
    if (dfn[u] == low[u]) {
        int v;
        do {
            v = st[top--];
            id[v] = 0;
        } while (v != u);
    }
}

int main() {
    scanf("%d%d%d", &n, &m, &q);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            scanf("%d", &s[i][j]);
            f[i][j] = f[i - 1][j] + f[i][j - 1] - f[i - 1][j - 1] + s[i][j];
        }
    }
    for (int i = 1; i <= q; i++) {
        int x1, y1, x2, y2;
        scanf("%d%d%d%d", &x1, &y1, &x2, &y2);
        int res = f[x2][y2] - f[x1 - 1][y2] - f[x2][y1 - 1] + f[x1 - 1][y1 - 1];
        for (int j = x1; j <= x2; j++) {
            for (int k = y1; k <= y2; k++) {
                if (s[j][k]) {
                    g[res].push_back(j * m + k);
                }
            }
        }
        tarjan(res);
        for (int j = x1; j <= x2; j++) {
            for (int k = y1; k <= y2; k++) {
                if (s[j][k]) {
                    g[res].clear();
                }
            }
        }
        ans[i] = res;
    }
    for (int i = 1; i <= q; i++) {
        printf("%d\n", ans[i]);
    }
    return 0;
}
