from collections import deque

def dfs(i, j, grid, visited):
    if 0 <= i < len(grid) and 0 <= j < len(grid[0]) and grid[i][j] == 1 and not visited[i][j]:
        visited[i][j] = True
        queue = deque([(i, j)])
        while queue:
            x, y = queue.popleft()
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < len(grid) and 0 <= ny < len(grid[0]) and grid[nx][ny] == 1 and not visited[nx][ny]:
                    visited[nx][ny] = True
                    queue.append((nx, ny))

def count_components(grid, x1, y1, x2, y2):
    visited = [[False] * len(grid[0]) for _ in range(len(grid))]
    components = 0
    for i in range(x1 - 1, x2):
        for j in range(y1 - 1, y2):
            if grid[i][j] == 1 and not visited[i][j]:
                dfs(i, j, grid, visited)
                components += 1
    return components

def main():
    n, m, q = map(int, input().split())
    grid = [list(map(int, input().split())) for _ in range(n)]
    for _ in range(q):
        x1, y1, x2, y2 = map(int, input().split())
        print(count_components(grid, x1, y1, x2, y2))

if __name__ == "__main__":
    main()
