public class Main {
    static int N, M, Q;
    static int[][] S;
    static int[][] ds;
    static int[] ds_size;
    static int[] parent;
    static int[] size;

    static int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    static void union(int x, int y) {
        int px = find(x);
        int py = find(y);
        if (px != py) {
            if (size[px] < size[py]) {
                int temp = px;
                px = py;
                py = temp;
            }
            parent[py] = px;
            size[px] += size[py];
            ds_size[px] += ds_size[py];
        }
    }

    static void dfs(int x, int y) {
        if (x < 1 || x > N || y < 1 || y > M || S[x][y] == 0) {
            return;
        }
        ds[x][y] = 1;
        size[find(x * M + y)]++;
        dfs(x - 1, y);
        dfs(x + 1, y);
        dfs(x, y - 1);
        dfs(x, y + 1);
    }

    static void solve() throws IOException {
        N = nextInt();
        M = nextInt();
        Q = nextInt();
        S = new int[N + 1][M + 1];
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= M; j++) {
                S[i][j] = nextInt();
            }
        }
        ds = new int[N + 1][M + 1];
        ds_size = new int[N * M + 1];
        parent = new int[N * M + 1];
        size = new int[N * M + 1];
        for (int i = 1; i <= N * M; i++) {
            parent[i] = i;
            size[i] = 0;
            ds_size[i] = 0;
        }
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= M; j++) {
                if (S[i][j] == 1) {
                    dfs(i, j);
                }
            }
