typedef struct {
    int x, v;
} Node;

int cmp(const void *a, const void *b) {
    Node *x = (Node *)a;
    Node *y = (Node *)b;
    return (x->v - y->v);
}

int main() {
    int n;
    scanf("%d", &n);
    Node *a = (Node *)malloc(n * sizeof(Node));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &a[i].x, &a[i].v);
    }
    qsort(a, n, sizeof(Node), cmp);
    int ans = 1;
    int cur = 0;
    for (int i = 0; i < n; i++) {
        if (a[i].x > cur) {
            ans = (ans * (i - cur + 1)) % MOD;
            cur = i;
        }
    }
    ans = (ans * (1 << (n - cur))) % MOD;
    printf("%d\n", ans);
    free(a);
    return 0;
}
