```java
import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 200005;

    static int n;
    static int[] x = new int[MAXN];
    static int[] v = new int[MAXN];
    static int[] p = new int[MAXN];
    static int[] cnt = new int[MAXN];
    static int[] fac = new int[MAXN];
    static int[] inv = new int[MAXN];

    static int qpow(int a, int b) {
        int res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = (int) ((long) res * a % MOD);
            a = (int) ((long) a * a % MOD);
            b >>= 1;
        }
        return res;
    }

    static void init() {
        fac[0] = 1;
        for (int i = 1; i <= n; i++) fac[i] = (int) ((long) fac[i - 1] * i % MOD);
        inv[n] = qpow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) inv[i] = (int) ((long) inv[i + 1] * (i + 1) % MOD);
    }

    static int C(int n, int m) {
        if (n < m) return 0;
        return (int) ((long) fac[n] * inv[m] % MOD * inv[n - m] % MOD);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(in.readLine());
        n = Integer.parseInt(st.nextToken());
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(in.readLine());
            x[i] = Integer.parseInt(st.nextToken());
            v[i] = Integer.parseInt(st.nextToken());
        }
        Arrays.sort(x, 1, n + 1);
        Arrays.sort(v, 1, n + 1);
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            p[i] = p[i - 1];
            while (p[i] < n && v[p[i] + 1] <= v[i]) p[i]++;
            cnt[i] = (int) ((long) C(n, p[i]) * fac[p[i]] % MOD * inv[n - p[i]] % MOD);
        }
        for (int i = 1; i <= n; i++) {
            ans = (ans + cnt[i]) % MOD;
        }
        out.println(ans);
        out.close();
    }
}
