#define MOD 1000000007

typedef struct {
    long long x, y;
} pair;

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

long long count(long long x, long long y) {
    long long g = gcd(x, y);
    long long ans = 0;
    for (long long i = 1; i * i <= g; i++) {
        if (g % i == 0) {
            long long a = i, b = g / i;
            long long c = (x / a) - (x / (a + 1)) + 1;
            long long d = (y / b) - (y / (b + 1)) + 1;
            ans = (ans + c * d) % MOD;
        }
    }
    return ans;
}

int main() {
    int q;
    scanf("%d", &q);
    pair a[q];
    for (int i = 0; i < q; i++) {
        scanf("%lld %lld", &a[i].x, &a[i].y);
    }
    for (int i = 0; i < q; i++) {
        long long x = a[i].x, y = a[i].y;
        long long g = gcd(x, y);
        long long ans = 0;
        for (long long j = 1; j * j <= g; j++) {
            if (g % j == 0) {
                long long a = j, b = g / j;
                long long c = (x / a) - (x / (a + 1)) + 1;
                long long d = (y / b) - (y / (b + 1)) + 1;
                ans = (ans + c * d) % MOD;
            }
        }
        printf("%lld %lld\n", (x + y) / 2 - 1, count(x, y));
    }
    return 0;
}
