    int q;
    cin >> q;

    vector<long long> x(q), y(q);
    for (int i = 0; i < q; ++i) {
        cin >> x[i] >> y[i];
    }

    vector<pair<long long, long long>> ans(q);
    for (int i = 0; i < q; ++i) {
        long long X = x[i], Y = y[i];
        long long gcd = X;
        long long lcm = Y;
        while (Y) {
            long long temp = Y;
            Y = X % Y;
            X = temp;
        }
        gcd = X;
        lcm = (X * Y) / gcd;

        long long max_steps = 0;
        long long cnt = 0;
        for (long long a = 1; a <= X; ++a) {
            long long b = (lcm / a) - 1;
            if (b >= 1 && b <= Y) {
                long long steps = 0;
                while (a != gcd) {
                    long long temp = a;
                    a = b % a;
                    b = temp;
                    steps++;
                }
                if (steps > max_steps) {
                    max_steps = steps;
                    cnt = 1;
                } else if (steps == max_steps) {
                    cnt = (cnt + 1) % MOD;
                }
            }
        }
        ans[i] = {max_steps, cnt};
    }

    for (auto& [steps, cnt] : ans) {
        cout << steps << ' ' << cnt << '\n';
    }

    return 0;
}
