int pre[N][N][4][4];

int main() {
    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        cin >> l[i] >> r[i] >> x[i];
    }

    memset(dp, 0, sizeof(dp));
    dp[0][0][0][0] = 1;

    for (int i = 1; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= 3; k++) {
                for (int t = 0; t <= 3; t++) {
                    if (dp[j][i - 1][k][t]) {
                        for (int p = 0; p <= 3; p++) {
                            if (p != k && p != t) {
                                int nxt = j + (r[i] - l[i] + 1) - (l[i] - 1 - j);
                                if (nxt <= r[i]) {
                                    pre[nxt][i][p][t] = (pre[nxt][i][p][t] + dp[j][i - 1][k][t]) % MOD;
                                }
                            }
                        }
                    }
                }
            }
        }

        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= 3; k++) {
                for (int t = 0; t <= 3; t++) {
                    dp[j][i][k][t] = pre[j][i][k][t];
                    pre[j][i][k][t] = 0;
                }
            }
        }
    }

    int ans = 0;
    for (int j = 0; j <= n; j++) {
        for (int k = 0; k <= 3; k++) {
            for (int t = 0; t <= 3; t++) {
                if (k != t && k != x[i] && t != x[i]) {
                    ans = (ans + dp[j][m][k][t]) % MOD;
                }
            }
        }
    }

    cout << ans << endl;

    return 0;
}
