MOD = 10**9 + 7

def add(a, b):
    return (a + b) % MOD

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def solve():
    N, M = map(int, input().split())
    intervals = []
    for _ in range(M):
        l, r, x = map(int, input().split())
        intervals.append((l, r, x))

    intervals.sort(key=lambda x: x[1])

    dp = [[0] * (N + 1) for _ in range(4)]
    dp[0][0] = 1

    for i in range(M):
        for j in range(4):
            for k in range(intervals[i][1] - 1, -1, -1):
                dp[j][k] = 0
        for j in range(4):
            for k in range(intervals[i][1] - 1, -1, -1):
                if dp[j][k]:
                    for l in range(1, intervals[i][2]):
                        dp[j][k + l] = add(dp[j][k + l], dp[j][k])
                    if intervals[i][2] == 1:
                        dp[j][k] = 0
                    elif intervals[i][2] == 2:
                        dp[j + 1][k] = add(dp[j + 1][k], dp[j][k])
                    elif intervals[i][2] == 3:
                        for l in range(1, intervals[i][2]):
                            dp[j + 1][k] = add(dp[j + 1][k], dp[j][k])
                            dp[j + 2][k] = add(dp[j + 2][k], dp[j][k])
                            dp[j][k] = 0
                            break

    ans = 0
    for i in range(4):
        ans = add(ans, dp[i][N])
    print(ans)

if __name__ == "__main__":
    solve()
