#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 105
#define MAX_W 105

int h, w;
char grid[MAX_H][MAX_W];
int visited[MAX_H][MAX_W];
int dist[MAX_H][MAX_W];

int bfs(int x, int y) {
    int i, j, dx[4] = {0, 0, 1, -1}, dy[4] = {1, -1, 0, 0};
    int q[1000], head = 0, tail = 0;
    q[tail++] = x;
    q[tail++] = y;
    memset(visited, 0, sizeof(visited));
    visited[x][y] = 1;
    dist[x][y] = 0;

    while (head < tail) {
        int x = q[head++];
        int y = q[head++];

        for (i = 0; i < 4; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];

            if (nx >= 0 && nx < h && ny >= 0 && ny < w && grid[nx][ny] != 'T' && !visited[nx][ny]) {
                visited[nx][ny] = 1;
                dist[nx][ny] = dist[x][y] + 1;
                q[tail++] = nx;
                q[tail++] = ny;
            }
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'o' && dist[i][j] == 0) {
                return -1;
            }
        }
    }

    int count = 0;
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'o' && dist[i][j] != 0) {
                count++;
            }
        }
    }

    return count;
}

int main() {
    int i, j;
    scanf("%d %d", &h, &w);
    for (i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }

    int x, y;
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'S') {
                x = i;
                y = j;
            }
        }
    }

    int ans = bfs(x, y);
    printf("%d\n", ans);

    return 0;
}
