```python
from collections import deque

def bfs(s, t, grid):
    rows, cols = len(grid), len(grid[0])
    visited = [[False]*cols for _ in range(rows)]
    queue = deque([(s, 0)])
    visited[s[0]][s[1]] = True
    while queue:
        (x, y), dist = queue.popleft()
        if (x, y) == t:
            return dist
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < rows and 0 <= ny < cols and grid[nx][ny] != 'o' and not visited[nx][ny]:
                queue.append(((nx, ny), dist + 1))
                visited[nx][ny] = True
    return -1

def solve():
    rows, cols = map(int, input().split())
    grid = [list(input()) for _ in range(rows)]
    s, t = None, None
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == 'S':
                s = (i, j)
            elif grid[i][j] == 'T':
                t = (i, j)
    if s[0] == t[0] or s[1] == t[1]:
        print(1)
        return
    dist = bfs(s, t, grid)
    if dist == -1:
        print(-1)
    else:
        print(rows * cols - 1 - dist)

solve()
