#include <limits>

using namespace std;

const long long INF = numeric_limits<long long>::max();

struct Edge {
    int to;
    long long cost;
};

int main() {
    int N, M;
    cin >> N >> M;

    vector<vector<Edge>> graph(N + 1);
    for (int i = 0; i < M; i++) {
        int a, b;
        long long c;
        cin >> a >> b >> c;
        graph[a].push_back({b, c});
    }

    vector<long long> dp(N + 1, -INF);
    dp[1] = 0;

    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>> pq;
    pq.push({0, 1});

    while (!pq.empty()) {
        auto [cost, node] = pq.top();
        pq.pop();

        if (cost != dp[node]) continue;

        for (const auto& edge : graph[node]) {
            int to = edge.to;
            long long c = edge.cost;

            if (dp[to] < dp[node] + c) {
                dp[to] = dp[node] + c;
                pq.push({dp[to], to});
            }
        }
    }

    if (dp[N] == -INF) cout << "inf" << endl;
    else cout << dp[N] << endl;

    return 0;
}
