        while (a % 2 == 0 && b % 2 == 0 && c % 2 == 0) {
            a /= 2;
            b /= 2;
            c /= 2;
            steps++;
        }

        if (a % 2 == 0 && b % 2 == 0 && c % 2 == 0) {
            System.out.println("-1");
        } else {
            long min = Math.min(a, Math.min(b, c));
            long max = Math.max(a, Math.max(b, c));
            long mid = (a + b + c - min - max) / 2;
            long diff = max - mid;
            long steps2 = 0;
            while (diff % 2 == 0 && min % 2 == 0) {
                min /= 2;
                diff /= 2;
                steps2++;
            }
            if (diff % 2 == 0 && min % 2 == 0) {
                System.out.println(steps + steps2 + "-1");
            } else {
                System.out.println(steps + steps2);
            }
        }
    }
}
